import UIKit

protocol ResultBusinessLogic
{
  func doSomething(request: Result.Something.Request)
}

protocol ResultDataStore
{
  //var name: String { get set }
    var name: String { get }
}

class ResultInteractor: ResultBusinessLogic, ResultDataStore
{
  var presenter: ResultPresentationLogic?
  var worker: ResultWorker?
    var name: String = ""
  //var name: String = ""
  
  // MARK: Do something
  
  func doSomething(request: Result.Something.Request)
  {
    worker = ResultWorker()
    worker?.doSomeWork()
    
    let response = Result.Something.Response()
    presenter?.presentSomething(response: response)
  }
}


