import UIKit

protocol ResultPresentationLogic
{
  func presentSomething(response: Result.Something.Response)
}

class ResultPresenter: ResultPresentationLogic
{
  weak var viewController: ResultDisplayLogic?
  
  // MARK: Do something
  
  func presentSomething(response: Result.Something.Response)
  {
    let viewModel = Result.Something.ViewModel()
    viewController?.displaySomething(viewModel: viewModel)
  }
}
